INSERT INTO `abouts` (`id`, `Title`, `Image`, `Nama`, `Deskripsi`, `Alamat`, `Email`, `created_at`, `updated_at`) VALUES
(1, 'ABOUT ME', 'Baru.jpg', 'Fiqih Ramdhani', 'Saya adalah Fiqih Ramdhani umur 25 Tahun, kegiatan sehari hari saya adalah  membuat program berskala kecil untuk melatih kemampuan saya dalam web development. tujuan saya membuat website tentang Pesona Indonesia karena Saya sangat mencintai Negara Indonesia. Indonesia mempunyai banyak Budaya dan Keindahan alam yang harus diekspos. banyak orang luar yang belum mengetahui betapa indahnya Indonesia . Selain kemampuan saya dalam WEB DEVELOPMENT , Saya juga tertarik untuk  mendaki gunung yang ada di Indonesia . Pengalaman pertama Saya mendaki gunung yaitu Gunung Salak. Sering kita jumpai akan mitos-mitos yang beredar di Masyarakat tentang Gunung Salak. Menurut Pengalaman Saya Gunung Salak sangat nikmat untuk kita kunjungi, sebab selama pendakian dan trek yang saya telusuri banyak sumber air. Tapi kekurangannya adalah jalur dan trek yang dikelilingi oleh Hutan . Jika ingin mendaki , saran saya untuk para pemula haruslah membawa teman yang sudah pernah mendaki Gunung Salak , agar kalian tidak tersesat dan sampai menuju Puncak Gunung Salak dengan selamat. ', 'Jakarta.11850 , Indonesia', 'fiqih.ramdhani1213@gmail.com', '2022-08-07 19:56:26', '2022-08-07 19:56:26'),
(2, 'ABOUT ME', 'MyPhoto.jpg', 'Fiqih Ramdhani', 'Hobby saya bermain gitar sebagimana uuntuk menghibur saya jika sedang suntuk. Saya Juga bisa bermain fingerstyle untuk beberapa lagu yang Saya kuasai. Saya juga menguasai beberapa keahlian diantaranya: MS.OFFICE, PHP, Java, Servis tv. Semua Saya pelajari karena saya tahu itu adalah Ilmu yang sangat berharga. untuk kedepannya Saya ingin menguasai beberapa bahasa pemrograman lain. Karna saya sangat programming merupakan sebuah passion untuk Saya. Masih banyak hal yang harus saya beritahu namun terlalu panjang jika di deskripsikan di sini semua.  ', 'Jakarta| 11850 |Indonesia', 'fiqih.ramdhani1213@gmail.com', '2022-08-07 19:56:37', '2022-08-07 19:56:37'),
(3, 'MY JOB', 'Indihome.png', 'Sales Indihome', 'Pengalaman kerja Saya sebagai Sales Indihome . Indihome adalah salah satu provider internet wifi milik Telkom Indonesia. Kecepatan yang di tawarkan mulai dari 20 mbps sampai 100 Mps. Dengan Harga yang terjangkau memungkin customer dapat menikmati layanan internet wifi Indihome sepuasnya. Harga Mulai dari 350 ribu per Bulan -  Hingga 800ribu perbulan dengan kecepatan 100 mbps.  ', 'Jakarta | 11850 | Indonesia', 'fiqih.ramdhani1213@gmail.com', '2022-08-07 19:56:47', '2022-08-07 19:56:47'),
(4, 'MY JOB', 'Kompas.png', 'Interviewer', 'Pengalaman kerja Saya berikutnya adalah sebagai Interviwer Polling Kompas . Saya senang karna pernah menjadi bagian dari kompas. lingkungan kerja yang nyaman membuat Saya ingin kembali Partime disana. pekerjaan yang saya lakukan adalah mencari informasi kepada Narasumber tentang berita yang sedang hangat di perbincangkan . Selain itu saya juga pernah terjun kelapangan untuk mewawancarai narasumber tentang penyakit kusta. lokasi tempat saya kerja tepat di belakang RS Sitanala.  ', 'Jakarta | 11850 | Indonesia', 'fiqih.ramdhani1213@gmail.com', '2022-08-07 19:56:57', '2022-08-07 19:56:57');

